// Fonction pour g√©n√©rer toutes les combinaisons et les r√©partir en 3 tableaux
function generateCombinationTables(item) {
  const analysis = item.json.output;
  const { problemes, segments, value_propositions } = analysis;

  // G√©n√©rer toutes les 27 combinaisons (3√ó3√ó3)
  const allCombinations = [];

  problemes.forEach(probleme => {
    segments.forEach(segment => {
      value_propositions.forEach(vp => {
        allCombinations.push({
          combination_id: `${probleme.id}_${segment.id}_${vp.id}`,
          probleme: {
            id: probleme.id,
            titre: probleme.titre,
            description: probleme.description,
            intensite: probleme.intensite_estimee,
            mesurable_par: probleme.mesurable_par
          },
          segment: {
            id: segment.id,
            nom: segment.nom,
            description: segment.description,
            taille_estimee: segment.taille_estimee,
            canaux_acces: segment.canaux_acces,
            comportements_cles: segment.comportements_cles,
            market_type: segment.market_type
          },
          value_proposition: {
            id: vp.id,
            titre: vp.titre,
            description: vp.description,
            differenciateur: vp.differenciateur,
            preuve_valeur: vp.preuve_valeur,
            key_benefits:vp.key_benefits
          }
        });
      });
    });
  });

  // R√©partir les 27 combinaisons en 3 tableaux de 9
  const table1 = allCombinations.slice(0, 9);   // Combinaisons 1-9
  const table2 = allCombinations.slice(9, 18);  // Combinaisons 10-18
  const table3 = allCombinations.slice(18, 27); // Combinaisons 19-27

  return {
    metadata: {
      projet: analysis.metadata.projet,
      total_combinaisons: analysis.metadata.total_combinaisons,
      genere_le: analysis.metadata.genere_le,
      repartition: "3 tableaux de 9 combinaisons chacun"
    },
    tableau_1: {
      nom: "Combinaisons 1-9",
      description: "Premier lot de combinaisons Probl√®me √ó Segment √ó Value Proposition",
      combinaisons: table1
    },
    tableau_2: {
      nom: "Combinaisons 10-18", 
      description: "Deuxi√®me lot de combinaisons Probl√®me √ó Segment √ó Value Proposition",
      combinaisons: table2
    },
    tableau_3: {
      nom: "Combinaisons 19-27",
      description: "Troisi√®me lot de combinaisons Probl√®me √ó Segment √ó Value Proposition", 
      combinaisons: table3
    }
  };
}

// Traiter les donn√©es
